import React from 'react';
import TodoItem from './TodoItem';
import userData from "./userData"

const styles = {
    backgroundColor: '#AFEEEE'
}

function handleClick() {
    console.log("I was clicked!")
}

class App extends React.Component {
    constructor () {
        super()
        this.state = {
            data: userData
        }
        //this.handleChange = this.handleChange(this)
    }
    // handleChange (id) {
    //     if (this.state.data.id.completed == true) {
    //         this.state.data.id.completed = false
    //     } else {
    //         this.state.data.id.completed = true
    //     }
    // }
    render () {
        const userComponents = this.state.data.map(item => <li><TodoItem key={item.id} user={item} /></li>)
        return(
            <div className='wrapper'>
                <h2>Todo List: {this.state.data.id}</h2> 
                <ol style={styles}>
                    {userComponents}
                </ol>
            </div>
        )
    }
};

export default App;
