import React from 'react';
import TodoItem from './TodoItem';
import userData from "./userData"

const styles = {
    backgroundColor: '#AFEEEE'
}

function handleClick() {
    console.log("I was clicked!")
}

class App extends React.Component {
    constructor () {
        super()
        this.state = {
            data: userData
        }
    }
    render () {
        const userComponents = this.state.data.map(item => <li><TodoItem key={item.id} user={item} /></li>)
        return(
            // <div className='wrapper'>
            //     <h2>Todo List:</h2> 
            //     <ol style={styles}>
            //         {userComponents}
            //     </ol>
            // </div>
            <div>
                <img onMouseOver={() => console.log("Hovered!")} src="https://avatars.githubusercontent.com/u/174257174?v=4"/> 
                <br /> 
                <br /> 
                <button onClick={handleClick}>Click me</button> 
            </div>
        )
    }
};

export default App;
