import React from 'react';
import TodoItem from './TodoItem';
import userData from "./userData"

const styles = {
    backgroundColor: '#AFEEEE'
}

function handleClick() {
    console.log("I was clicked!")
}

class App extends React.Component {
    constructor () {
        super()
        this.state = {
            data: userData
        }
        this.handleChange = this.handleChange.bind(this)
    }
    handleChange (id) {
        let newCompl;
        if (id.completed === true) {
            newCompl = false
        } else {
            newCompl = true
        }
        id.setState(completed => {
            return {
                completed: newCompl
            }
        })
    }
    render () {
        const userComponents = this.state.data.map(item => <li><TodoItem key={item.id} user={item} handleChange={handleClick} /></li>)
        return(
            <div className='wrapper'>
                <h2>Todo List:</h2> 
                <ol style={styles}>
                    {userComponents}
                </ol>
            </div>
        )
    }
};

export default App;
